</span></span><span style="display:flex;"><span><span style="color:#228b22">// 创建连接
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span><span style="color:#00688b">$conn</span> = <span style="color:#8b008b;font-weight:bold">new</span> mysqli(<span style="color:#00688b">$servername</span>, <span style="color:#00688b">$username</span>, <span style="color:#00688b">$password</span>, <span style="color:#00688b">$dbname</span>);
</span></span><span style="display:flex;"><span> 
</span></span><span style="display:flex;"><span><span style="color:#228b22">// 检测连接
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span><span style="color:#8b008b;font-weight:bold">if</span> (<span style="color:#00688b">$conn</span>-&gt;<span style="color:#658b00">connect_error</span>) {
</span></span><span style="display:flex;"><span>    <span style="color:#8b008b;font-weight:bold">die</span>(<span style="color:#cd5555">&#34;连接失败: &#34;</span> . <span style="color:#00688b">$conn</span>-&gt;<span style="color:#658b00">connect_error</span>);
</span></span><span style="display:flex;"><span>}
</span></span><span style="display:flex;"><span>
</span></span><span style="display:flex;"><span><span style="color:#228b22">// imgmd5 名称转md5
</span></span></span><span style="display:flex;"><span><span style="color:#228b22">// imguploadtime 上传时间戳
</span></span></span><span style="display:flex;"><span><span style="color:#228b22">// imgurl 链接
</span></span></span><span style="display:flex;"><span><span style="color:#228b22">// 上传 ip
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span><span style="color:#00688b">$sql</span> = <span style="color:#cd5555">&#34;select imgmd5,imguploadtime,imgurl,imguploadip from remote_imgs
</span></span></span><span style="display:flex;"><span><span style="color:#cd5555">&#34;</span>;
</span></span><span style="display:flex;"><span><span style="color:#00688b">$result</span> = <span style="color:#00688b">$conn</span>-&gt;<span style="color:#658b00">query</span>(<span style="color:#00688b">$sql</span>);
</span></span><span style="display:flex;"><span>
</span></span><span style="display:flex;"><span>
</span></span><span style="display:flex;"><span><span style="color:#8b008b;font-weight:bold">if</span> (<span style="color:#00688b">$result</span>-&gt;<span style="color:#658b00">num_rows</span> &gt; <span style="color:#b452cd">0</span>) {
</span></span><span style="display:flex;"><span>    <span style="color:#228b22">// 输出数据
</span></span></span><span style="display:flex;"><span><span style="color:#228b22"></span>    <span style="color:#8b008b;font-weight:bold">while</span>(<span style="color:#00688b">$row</span> = <span style="color:#00688b">$result</span>-&gt;<span style="color:#658b00">fetch_assoc</span>()) {
